import react,{Component} from 'react'
import Table from './Table'

class App extends Component{
  state = {
    names :[
      {
        name: 'Riddhi',
        age : 22
      },
      {
        name : 'ABC',
        age : 34
      }
    ]
  }

  removeName = (index) =>{
    const {chars} = this.state
    this.setState({
      chars : chars.filter((character,i)=>{
        return i !== index
      }),
    })
  }
    render(){

      const {names} = this.state
      // console.log({names});
      return (
        <div className = 'App'> 
        <Table names = {names} removeName = {this.removeName} />
        </div>
      )
    }
  }
  export default App
